/** allows imports of CSS form TS/TSX */
declare module '*.css' {
  const value: any;
  export default value;
}

declare module '*.svg';
declare module '*.svg?include';
declare module '*.svg?webp';
declare module '*.svg?inline';
declare module '*.svg?url';
declare module '*.svg?original';
declare module '*.svg?sprite';
declare module '*.webp';
declare module '*.png';
declare module '*.mp4';
declare module '*.webm';
declare module '*.jpg';
declare module '*.gif';
